INSERT INTO `creature_ai_texts` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES (-264,'I feel that war is fast approaching. The fruit vendor has enlisted to help an ogre. I shall have to enlist help as well.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0,0,0,0,'19196');
